import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const CreateForm = () => {
  const [title, setTitle] = useState("Untitled Form"); // initial form title;
  const [inputs, setInputs] = useState([]); //state for managing input fields
  const [isEditing, setIsEditing] = useState(false); // state to toggle form editor
  const [showInputOptions, setShowInputOptions] = useState(false); // state toggle input options
  const [editingIndex, setEditingIndex] = useState(null); // index of the input field
  const [currentTitle, setCurrentTitle] = useState(""); // title of the input being edited
  const [currentPlaceholder, setCurrentPlaceholder] = useState("");
  const navigate = useNavigate();

  // toggle visibility of input type buttons
  const toggleInputOptions = () => {
    setShowInputOptions(!showInputOptions);
  };

  // Handle adding a new input field
  const addInput = (type) => {
    if (inputs.length < 20) {
      // limit to 20 inputs
      setInputs([...inputs, { type, title: "", placeholder: "" }]);
    }
  };

  // handle editing an input field
  const editButtonHandler = (index) => {
    setIsEditing(true);
    setEditingIndex(index) // set the index of the input field being edited
    setCurrentTitle(inputs[index].title)
    setCurrentPlaceholder(inputs)
  }

  // Handle deleting an input field
  const deleteInput = (index) => {
    const newInputs = inputs.filter((_, i) => i !== index);
  };

  // Handle Form submission
  const handleCreateForm = () => {
    // for now just navigate to the home page
    navigate("/");
  };

  return (
    <div>
      <h1>Create New form</h1>
      <div
        style={{ display: "flex", padding: "20px", border: "1px solid #ccc" }}
      >
        {/* left container */}
        <div
          style={{ flex: 1, padding: "10px", borderRight: "1px solid #ccc" }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <h2>{title}</h2>
            <button onClick={() => setIsEditing(!isEditing)}>Edit</button>
          </div>

          {inputs.map((input, index) => (
            <div key={index} style={{ marginBottom: "10px" }}>
              <input
                type={input.type}
                value={input.title}
                placeholder={input.placeholder}
                readOnly
                style={{ width: "100%", marginBottom: "5px" }}
              />
              <button>Edit</button>
              <button onClick={() => deleteInput(index)}>Delete</button>
            </div>
          ))}

          <div>
            <button type="button" onClick={toggleInputOptions}>
              {showInputOptions ? "CLOSE ADD INPUT" : "ADD INPUT"}
            </button>
          </div>
          <div>
            {showInputOptions && (
              <div style={{ marginTop: "10px" }}>
                <button type="button" onClick={() => addInput("text")}>
                  Text
                </button>
                <button type="button" onClick={() => addInput("number")}>
                  Number
                </button>
                <button type="button" onClick={() => addInput("email")}>
                  Email
                </button>
                <button type="button" onClick={() => addInput("password")}>
                  Password
                </button>
                <button type="button" onClick={() => addInput("date")}>
                  Date
                </button>
              </div>
            )}
          </div>
        </div>
        {/* right container */}

        <div style={{ flex: 1, padding: "10px" }}>
          {/* right container content */}
          <h2>Form Editor</h2>
          {isEditing ? (
            <label>
              Title:
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Title"
              />
            </label>
          ) : (
            <p>Select to see editor</p>
          )}
        </div>
      </div>

      <button
        type="button"
        onClick={handleCreateForm}
        style={{ marginTop: "20px" }}
      >
        Create Form
      </button>
    </div>
  );
};

export default CreateForm;
