import React, { useState } from "react";

const ConditionalRendering = () => {
  // State to manage visibility of each div
  const [visibleDiv, setVisibleDiv] = useState(null);

  return (
    <div>
      {/* Buttons to toggle state for demonstration */}
      <button onClick={() => setVisibleDiv("first")}>Show First Div</button>
      <button onClick={() => setVisibleDiv("second")}>Show Second Div</button>
      <button onClick={() => setVisibleDiv(null)}>Show No Content</button>

      {/* Conditional Rendering */}
      {visibleDiv === "first" && <div>First Div Content</div>}
      {visibleDiv === "second" && <div>Second Div Content</div>}
      {visibleDiv === null && <div>No content</div>}
    </div>
  );
};

export default ConditionalRendering;
