  return (
    <div>
      <h1>Create New form</h1>
      <div style={{ display: "flex", padding: "20px", border:"1px solid #ccc" }}>
        {/* left container */}
        <div
          style={{ flex: 1, padding: "10px", borderRight: "1px solid #ccc" }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <h2>Survey Form</h2>
            <button>Edit</button>
          </div>
          <div>
            <button type="button">ADD INPUT</button>
          </div>
        </div>
        {/* right container */}

        <div style={{ flex: 1, padding: "10px" }}>
          {/* right container content */}
            <h2>Form Editor</h2>


            <p>Select to see editor</p>

            

        </div>
      </div>

      <button
        type="button"
        onClick={handleCreateForm}
        style={{ marginTop: "20px" }}
      >
        Create Form
      </button>
    </div>
  );
};

export default CreateForm;
