import React from "react";

const FormInput = ({ type, value, placeholder, readonly, mode }) => {
  let inputType = type;

  // Determine the input type based on the type group

  if (mode === "edit") {
    if (type === "password") {
      inputType = 'text';
    } else if (type === "date") {
      inputType = text;
    } else {
      inputType = "text"; // Default to text for view mode
    }
  } else {
    inputType = type; // Use the type provided for edit mode
  }

  return (
    <input
      type={inputType}
      value={value}
      placeholder={placeholder}
      readOnly={readonly}
      style={{ width: "100%", marginBottom: "5px" }}
    />
  );
};

export default FormInput;
