import React from "react";
import { inputTypeConfig } from "./InputTypeConfig";

const ViewForm = ({ type, title, placeholder, viewMode }) => {
  // Get the configuration for the given type
  const { type: inputType, readOnly } = inputTypeConfig[type] || {};

  return (
    <input
      type={inputType}
      value={viewMode === "view" ? "" : title}
      placeholder={placeholder}
      readOnly={viewMode === "view" || readOnly}
    />
  );
};

export default ViewForm;
