import React, {useState, useEffect} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';

const Home = ()=> {
    const [forms, setForms] =useState([]);

    useEffect(()=> {
        axios.get('http://localhost:5000/api/forms')
        .then(response => setForms(response.data))
        .catch(error => console.error('Error fetching forms', error))
        
        
    },[]);

    //function to handle deleting a form
    const handleDelete = (id) => {
        axios.delete(`http://localhost:5000/api/form/${id}`)
        .then(()=> {
            setForms(forms.filter(form => form._id !== id))
        })
        .catch(error => console.error('Error deleting form:', error));
        
    }

        return (
            <div>
                <h1>welcome to Form.com</h1>
                <h5>This is a simple form builder</h5>
                <Link to='/form/create'>Create new form</Link>

                {forms.length === 0 ? (
                    <p>You have no forms created yet</p>
                ) : (
                    <ul>
                        {forms.map(form=> (
                            <li key={form._id}>
                                <h2>{form.title}</h2>
                                <div>
                                    <Link to={`/form/${form._id}`}>View</Link>
                                    <Link to={`/form/${form._id}/edit`}>Edit</Link>
                                    <button onClick={() => handleDelete(form._id)}>Delete</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        )

}

export default Home;
