import React from "react";

const FormInput = ({type, value, placeholder, readonly, onchavienge})=> {
    let inputType = type;

    // Determine the input type based on the type group

    if (mode === 'view')
    if(type === 'password') {
        inputType = 'password'
    } else if (type === 'date') {
        inputType ='date'
    }else {
        inputType = type; 
    }

    return (
        <input
        type={inputType}
        value={value}
        placeholder={placeholder}
        readOnly={readonly}
        
        style={{width: "100%", marginBottom: "5px"}}
        />
    )
}

export default  FormInput;
